import { Reducer, SetStoreAction } from './types';
export default function createStore<TStore, TAction = SetStoreAction<TStore>>(initialStore: TStore, reducer?: Reducer<TStore, TAction>): readonly [{
    (): TStore;
    <T>(selectorFn: (store: TStore) => T): T;
}, (action: TAction) => void, () => TStore];
